from File_Manager import FM
import classes

class Ui_MainWindow(object):
    cafe=classes.Driver()
    def open_dashboard(self,MainWindow,uId):
        
        self.window=QtWidgets.QMainWindow()
        self.ui=Ui_costumer_dashboard()
        self.ui.setupUi(self.window)
        self.ui.userId=uId
        self.ui.load_data()
        print(self.ui.userId)
        self.window.show()
        MainWindow.close()
        


    def open_signUp(self,MainWindow):
        result = QMessageBox.question(None, "Confirmation", "Are you an Employee?", QMessageBox.Yes | QMessageBox.No)
        MainWindow.close()
        if result==QMessageBox.Yes:
            self.window=QtWidgets.QMainWindow()
            self.ui=emp_SignUP()
            self.ui.setupUi(self.window)
            self.window.show()
        else:
            self.window=QtWidgets.QMainWindow()
            self.ui=Ui_Sign_up()
            self.ui.setupUi(self.window)
            self.window.show()



    def load_users(self):
        self.cafe.userList=FM.read_users()
        #self.textEdit_2.setEchoMode(QtGui.QLineEdit.Password)
        
       
    def login(self,MainWindow):
        if self.cafe.userList is not None:
            for user in self.cafe.userList:
                
                if user.username == self.textEdit.toPlainText() and user.password == self.textEdit_2.toPlainText():
                    self.open_dashboard(MainWindow,user.user_id)
                    return
                    
            self.show_Error_data_message("User record not found in system!!!\nMay be you want to signUp")
                    
        else:
            self.show_Error_data_message("User record not found in system!!!\nMay be you want to signUp")
                


    def show_Error_data_message(self,message=""):
        msg =QMessageBox()
        msg.setIcon(QMessageBox.Warning)
        msg.setText("Error")
        msg.setInformativeText(message)
        msg.setWindowTitle("Error")
        msg.exec_()

